                // Select item
                var selectionRangeRow = selectionRange[before ? 0 : selectionRange.length - 1];
                selectionRangeRow.selected = true;
                getSiblingsOf(selectionRangeRow, row, rangeQuery).forEach(function(item) {
                  item.selected = true;
                });
