        var cellWidth = parseFloat(computedStyle.width);
        var cellPadding = parseFloat(computedStyle.paddingLeft) + parseFloat(computedStyle.paddingRight);
        var borderRightWidth = parseFloat(computedStyle.borderRightWidth);

